INSERT INTO `monitor_analysis` VALUES (3610, '2023-10-16 19:51:04', 1713885267431264256, 1493155905573883904, 1, '{\"all\":{\"rate\":100,\"count\":5},\"earlyWarning\":{\"rate\":0.0,\"count\":0,\"dataSwitch\":\"close\"},\"noSensitive\":{\"rate\":80.0,\"count\":4},\"sensitive\":{\"rate\":20.0,\"count\":1}}', '{\"rate\":{\"negative\":\"20%\",\"neutral\":\"80%\",\"positive\":\"0.00%\"},\"chart\":[[\"正面\",0],[\"负面\",1],[\"中性\",4]]}', '[{\"rate\":\"60%\",\"count\":3,\"keyword\":\"舆情监测\"},{\"rate\":\"20%\",\"count\":1,\"keyword\":\"舆情监控\"},{\"rate\":\"20%\",\"count\":1,\"keyword\":\"舆情分析\"},{\"rate\":\"0.00%\",\"count\":0,\"keyword\":\"网络舆情监测\"},{\"rate\":\"0.00%\",\"count\":0,\"keyword\":\"舆情系统\"}]', '{\"text\":\"您一共设置了10个关键词。正面占比最高的是【舆情监测】到达0.00%。其次是【网络舆情监测】到达0.00%。负面占比最高的是【舆情分析】到达100%。其次是【舆情监测】到达0.00%。\",\"chart\":[[\"2023-10-15 22\",1,0],[\"2023-10-15 23\",0,0],[\"2023-10-16 00\",0,0],[\"2023-10-16 01\",0,0],[\"2023-10-16 02\",0,0],[\"2023-10-16 03\",0,0],[\"2023-10-16 04\",0,0],[\"2023-10-16 05\",0,0],[\"2023-10-16 06\",0,0],[\"2023-10-16 07\",0,0],[\"2023-10-16 08\",0,0],[\"2023-10-16 09\",0,0],[\"2023-10-16 10\",0,0],[\"2023-10-16 11\",0,1],[\"2023-10-16 12\",1,0],[\"2023-10-16 13\",0,0],[\"2023-10-16 14\",0,0],[\"2023-10-16 15\",1,0],[\"2023-10-16 16\",0,0],[\"2023-10-16 17\",1,0]]}', '{\"all\":[{\"emotion\":3,\"article_public_id\":\"a0e54034a4c9a9e18fe0c7a5848b639c\",\"publish_time\":\"2023-10-16 11:05:00\",\"key_words\":\"{\\\"服务投诉\\\":1,\\\"分析报告\\\":1,\\\"乱收费\\\":1,\\\"消费者\\\":3,\\\"商品类\\\":1,\\\"消保委\\\":1,\\\"许海燕\\\":1,\\\"上门费\\\":1,\\\"消费投诉\\\":2,\\\"第三季度\\\":2,\\\"受理投诉\\\":1}\",\"title\":\"三季度全省受理消费投诉5.5万件 为消费者挽损4783万余元\",\"_score\":\"13.73\",\"content\":\"(记者 许海燕 洪叶) 10月9日，省消保委发布第三季度消费投诉和舆情分析报告。第三季度，共受理投诉55545件，接待消费者来访和咨询9.75万人次，投诉数据同比下降13.97%，为消费者挽回经济损失4783万余元。其中，商品类投诉达27420件，占受理总量的49.37%;服务类达27558件，占比49.61%;其他商品和服务投诉达567件，占比1.02%。 报告显示，消费投诉热点主要涉及汽车、医疗、房产、教育、旅游等行业和领域。家电安装维修投诉多，存在免费安装却收上门费、虚假维修、巧立名目乱收费、冒充官方维修人员、售后服务差、不履行三包承诺等问题。研学游成为今年暑期热门话题，存在研而不学、打卡走形式、价格虚高、研学机构鱼龙混杂、导师资质、食宿安全等问题。房屋装修顽疾难破，设计师不专业、口头承诺没有写入合同、工程延期、价格超预算、恶意增项等现象频发，消费者面临维权难困境。共享电动车不配安全头盔、共享按摩椅服务问题和安全隐患、共享办公室纠纷等一系列共享经济领域问题频发。早教托育服务待规范，早教托育领域商家跑路、退费问题等引发舆论关注。\",\"source_name\":\"苏讯网\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"8d1d17dbf4627b988ccf9b8f02833aaa\",\"publish_time\":\"2023-10-16 17:54:03\",\"key_words\":\"{\\\"违法交易\\\":1,\\\"消费者权益保护法\\\":1,\\\"黑恶势力\\\":1,\\\"承诺书\\\":1,\\\"流通领域\\\":4,\\\"反不正当竞争法\\\":1,\\\"执法人员\\\":1,\\\"宣传活动\\\":1,\\\"新闻网讯\\\":1,\\\"无照经营\\\":1,\\\"扫黑除恶\\\":2,\\\"江西省泰和县市场监督管理局\\\":1,\\\"整治工作\\\":1,\\\"投诉举报\\\":2,\\\"新格局\\\":1}\",\"title\":\"江西省泰和县市场监管局持续推进市场流通领域扫黑除恶专项整治\",\"_score\":\"12.85\",\"content\":\"中国质量新闻网讯 为强化市场流通领域监管，进一步铲除黑恶势力滋生土壤，江西省泰和县市场监督管理局持续开展以市场流通领域为重点的专项整治工作，组织人员对全县蔬菜、猪肉销售商户进行专项执法监督检查。 该局以群众关心的热点、难点及堵点问题为整治重点，持续打击市场流通领域欺行霸市、垄断、网络违法交易、侵权假冒及无照经营等违法行为。通过日常监管、重点整治、投诉举报、舆情监测等摸排方式，深挖涉黑涉恶线索。该局组织执法人员深入各农贸市场，积极开展《反有组织犯罪法》《消费者权益保护法》《反不正当竞争法》等法律法规宣传活动，加强内容解读，提高社会知晓度，营造群众懂法、学法、守法的浓厚氛围。引导经户签订合规经营承诺书，督促各类生产经营主体强化行业自律，构建社会关心、支持、参与市场流通领域监管的共治新格局。 下一步，该局将继续推进扫黑除恶与日常监管的融合，加大宣传力度，发挥投诉举报平台作用，做到黑恶线索及时掌握、及时处理、及时报送、及时移交，常态化推进扫黑除恶专项斗争，努力净化营商环境，全力维护社会和谐稳定，以实际成效增强人民群众的获得感、幸福感、安全感。（杨小儿 邹慧）\",\"source_name\":\"手机新浪网\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"162e3dace51142ffc05eefed4f07e298\",\"publish_time\":\"2023-10-16 15:15:00\",\"key_words\":\"{\\\"药安食美诚信\\\":1,\\\"药品监管\\\":5,\\\"微信公众号\\\":4,\\\"舆情监测系统\\\":2,\\\"中国医药报社\\\":1,\\\"周阅读量\\\":3,\\\"湖北药监\\\":2,\\\"人工耳蜗\\\":2,\\\"公众号\\\":6,\\\"食品药品\\\":2,\\\"国家药监局\\\":5,\\\"高质量发展\\\":2,\\\"中国健康传媒集团\\\":2}\",\"title\":\"全国省级药监微信公众号榜单｜10月8日—10月14日周榜揭晓\",\"_score\":\"11.85\",\"content\":\"10月15日，中国医药报社发布全国省级药监微信公众号周榜，该榜单由中国健康传媒集团食品药品舆情监测系统提供数据支持。 全国省级药监微信公众号排行周榜（10月8日—10月14日）如下： “海南药闻”排名第一，“湖北药监”排名第二，“广东药监”排名第三。 第四名到第十名的公众号分别是：“河南药品监管”“甘肃药品监管”“江西药品监管”“药安食美诚信河北”“吉林药监”“北京药监”“山东药品监管”。 中国健康传媒集团食品药品舆情监测系统显示，排名第一的“海南药闻”10月10日发布的文章《再传喜讯！又一真研试点品种获批上市》为该公众号当周阅读量最高的文章。医药产业高质量发展能更好地满足新时代人民群众更高水平的健康需求，相关话题和内容受到公众的广泛关注。2019年，国家药监局和海南省政府高位推动博鳌乐城国际医疗旅游先行区临床真实世界数据应用试点工作。目前，已形成了规范的制度体系、顺畅的运行机制、开放的数据平台和丰富的专家资源，在政策制度、技术标准、产品加快上市等方面所取得的一系列突破性的成果。截至目前，共有12个药械品种获批上市。此次获得国家药监局批准上市的是科利耳有限公司的人工耳蜗声音处理器，该产品可将接收到的声音进行转化处理，通过人工耳蜗植入体将电刺激传输至听神经，以恢复患者听力。 排名第二的“湖北药监”于10月8日发布的文章《咸宁开展药品安全交叉检查，发现问题63个，立案2起！》为该公众号当周阅读量较高的文章。为贯彻落实国家药监局、湖北省药监局关于开展药品安全巩固提升行动的工作部署。自7月份以来，咸宁市市场监管局组织抽调各县（市、区）监管业务骨干，开展药品安全交叉检查。以医疗机构周边、产业园区、农村地区等为重点区域，以疫苗、麻醉药品和精神药品等为重点品种，对药品零售企业和使用单位非法渠道购进以及通过网络异常销售为重点线索，对医疗机构和药品零售企业特殊药品管理、执业药师在职在岗等重点内容进行检查，对检查发现的风险隐患问题责令限期整改落实到位。 排名第三的“广东药监”于10月13日发布的文章《喜报！广东省药监局在这个评选中获“大满贯”》为该公众号当周阅读量较高的文章。近日，国家药监局通报2023年全国医疗器械安全宣传周活动情况。为总结和推广好的经验、做法，国家药监局组织开展了优秀组织单位、媒体报道优胜单位和优秀科普视频制作单位的评选，予以通报表扬。广东省药监局获得优秀组织奖、媒体报道优胜奖、科普视频优秀奖。今后，还将进一步加强科普宣传材料的制作，创新科普宣传的形式和手段，不断提高医疗器械安全科普宣传工作水平，为保障人民群众用械安全，促进医疗器械产业高质量发展作出更大贡献。 综合各省级药监微信公众号推送内容来看，10月13日，国家市场监督管理总局网站公布《药品经营和使用质量监督管理办法》，该信息备受药监系统关注，被“海南药闻”“甘肃药品监管”“安徽药品监管”等多家公众号转载。 此榜单的监测时段为10月8日—10月14日，相关数据截至10月14日24点。公众号排名根据总阅读数、最大阅读数、文章数、原创文章数等多维度综合得出。由于省级药监微信公众号的相关数据滚动更新，相关排名会根据时间产生变化。（李佳欢） 《中国医药报》社版权所有，未经许可不得转载使用。 (责任编辑：常靖婕)\",\"source_name\":\"手机新浪网\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"11d5b64c146b77cfb31b2ca733f2e539\",\"publish_time\":\"2023-10-15 22:17:00\",\"key_words\":\"{\\\"用户消费\\\":1,\\\"小游戏\\\":2,\\\"机器人\\\":8,\\\"管理工具\\\":2,\\\"游戏社区\\\":7,\\\"更好地\\\":3,\\\"社区活动\\\":1,\\\"自定义\\\":2,\\\"游戏厂商\\\":3,\\\"感受到\\\":1,\\\"数据分析\\\":2,\\\"创造力\\\":2,\\\"第三方应用程序\\\":1,\\\"活跃度\\\":3,\\\"我们可以\\\":6}\",\"title\":\"\\\"破解用户运营难题！Fanbook帮你提升社区运营效率\\\"\",\"_score\":\"10.86\",\"content\":\"原标题：\\\"破解用户运营难题！Fanbook帮你提升社区运营效率\\\" 你还在为这些游戏社区运营效率烦恼么 传统游戏社区在运营中面临的难题多种多样的，同时层出不穷无法满足用户的多种需求。传统社区通常只分为群主和管理员两个简单的身份权限，没有细分化的身份权限，社区的秩序混乱，我们无法有效管理游戏社区用户。同时内容呈现单一、用户消费场景单一、即时动态无法有效传递、群聊杂乱玩家易错过消息等等，这些都降低用户体验和社区的活跃度，无法达到用户存量的效果。在传统游戏社区运营中，数据反馈依赖截图，且需要人工统计，大大浪费人力资源且效率低下易出错。种种问题的出现，都在影响着我们的游戏社区运营效率。 面对社区运营难题，Fanbook教你如何用Bot解决 Fanbook作为一款多功能且创造力丰富的游戏社区管理工具，Bot就是运行在其内部的第三方应用程序，同时也是游戏社区管理工具的得力助手。用户可以通过发送消息、命令和内联请求与机器人互动。在机器人市场里面，我们可以根据自身需求选择相对应的机器人，帮助我们解决用户运营的难题，提升社区运营效率，构建完美社区。 系统化的积分体系，提高用户活跃：在机器人市场里面，选择要添加的机器人。点击添加按钮后，在上面的区域就显示所在服务器拥有的机器人了，然后在对应频道内@机器人，然后点击对应需要使用的功能即可添加积分任务机器人，生成积分商场。用户通过签到、积分、奖励兑换等渠道，提升用户的活跃度。这些便捷的功能可以激发用户的积极性，让他们参与社区更多的互动。同时让用户感受到自己的价值，从而更加投入地参与社区活动。我们也可以提供丰富的工具组合来快速实现运营活动的落地。通过抽奖、投票、扭蛋等多样化Bot功能，我们可以方便地发起活动、发布奖励，让用户参与更加简单。同时结合公告频道和日历频道，我们可以让用户清晰地了解参与方式和活动进程，提高活动的成功率。 数据分析和反馈收集，了解用户的活跃情况：Bot可以记录用户的行为和互动，并生成相关的报告和分析结果。这些数据可以帮助企业了解用户的需求和兴趣，从而做出相应的调整和优化。此外，Bot还可以帮助企业收集用户的反馈和意见，在运营中及时进行修改和改进。通过这些数据分析和反馈收集，企业可以更加直接地了解用户的想法和需求，从而更好地满足他们的期望。 Bot＋AI舆情监控，及时反馈舆情：Fanbook拥有强大的Bot能力，我们可以轻松实现24小时消息审核、情感监测和风向预警。我们可以添加消息审核Bot，让其自动审查不良发言并将其撤回，有效净化社区环境。同时，利用AI情感监测技术，我们可以关注用户热议问题，并监测热度攀升的问题，实时预警，缩短客户应对舆论的反应时间。 AI＋小游戏结合,丰富社区玩法：将AI能力与小游戏频道结合，可以为用户提供各种有趣的玩法，降低二创门槛。例如，Al绘图等有趣玩法可以让用户发挥创造力，与游戏内容有机结合，为社区增添乐趣和互动性。此外，丰富的小游戏玩法，如俄罗斯方块、五子棋，以及社区互动玩法，如海龟汤和成语接龙，都可以增加用户的参与度和粘性。 可自定义Bot功能，定制个性化运营工具：Fanbook允许游戏厂商根据自己的需求,自定义Bot的功能和运作行为，通过量身定制Bot，游戏厂商可以更好地满足不同期望，提供个性化的用户体验。这种针对性的定制使得游戏厂商能够更好地引导和管理社区用户，增加用户黏性和活跃度。\",\"source_name\":\"搜狐号\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"15fcd6bfe79c74baa079601056204e79\",\"publish_time\":\"2023-10-16 12:15:20\",\"key_words\":\"{\\\"上海市高级人民法院\\\":2,\\\"智慧法院\\\":5,\\\"标签化\\\":2,\\\"协助院\\\":1,\\\"上海高院\\\":1,\\\"为基础\\\":1,\\\"量身定制\\\":1,\\\"上海市嘉定区人民法院\\\":2,\\\"自动识别\\\":4,\\\"参考库\\\":2,\\\"审管办\\\":1,\\\"职能部门\\\":2,\\\"管理部门\\\":2,\\\"嘉定法院\\\":9,\\\"信息化\\\":7}\",\"title\":\"【建设实践】如何实现“四类案件”全流程监管？上海嘉定法院“独家秘籍”请查收！\",\"_score\":\"7.84\",\"content\":\"/ 智慧前言/ 为紧紧围绕“公正与效率”主题，推动完善审判权力运行和监督体系，上海市嘉定区人民法院在上海市高级人民法院的指导下，积极试点研发并全面运用“四类案件”信息化智能监管平台，建立“推送-标注-监管-落实监管-监管确认”的全流程信息化监管闭环体系，推动实现“四类案件”的自动识别、精准标注、实时预警、智能监管、全程留痕。 “四类案件”监管流程图 “数智”创新： 实现“四类案件”监管智能化 嘉定法院聚焦“四类案件”识别标注的精准及时性，细化识别指引，推动等级化、标签化监管，解决不善识别、不懂标注、需求变化等问题。 嘉定法院依托上海高院创建的四类案件参考库，将需监管的案件分为“四类案件”和八类参照适用的“其他重点案件”，细化了68项客观标准作为识别指引，根据参考库所列情形与“四类案件”进行匹配，区分高、中、低三个参考等级，明确不同等级的标注提示和监管规则，内嵌至“四类案件”信息化智能监管平台，形成三级目录标签化管理。 在平台自动识别模式下，能够对符合预警信息的案件及时推送提醒，同时联动舆情监测、信访督察等模块的各类资源，协助院庭长及时发现可能存在的监管风险点，有效解决信息不对称导致的监管盲区。 在人工识别模式下，平台将区分标注主体和推送主体，立案部门和审判部门可直接标注，院庭长、审管办、信访办、督察室等职能部门可推送预警，同时还可在“四类案件推送”模块的待标注列表中，对该院需要监管的案件进行推送。对于中等级案件确有必要纳入监管的，审判管理部门可以将其调整为高等级后进行复推，以避免涉监管案件发生“脱管”现象。 “数能”驱动： 实现“四类案件”监管精准化 嘉定法院聚焦“四类案件”监督管理的精准实效性，推进事中实时监管动态化，解决监管失灵、监管空转、监管放任等问题。 在精准适配监管措施方面，嘉定法院以最高人民法院、上海市高级人民法院的监管措施为基础，进一步细化“8+10”监管措施，明确监管正面和负面清单14条。结合院庭长46项正面权责清单，为每类案件量身定制监管方案指引并预设至平台，赋予监管措施拦截、跟踪、预警等不同强制力，为院庭长监管行权提供参考。 在优化动态监管方面，嘉定法院建立智能“防火墙”预警拦截机制，根据不同监管措施落实情况，平台将采取拦截进程、说明理由、报请审批、跟踪预警等措施，实现对“四类案件”的精准监管。 监管主体分配监管措施和确定预警节点 “数助”分析： 实现“四类案件”监管系统化 嘉定法院聚焦“四类案件”监督管理的数据交互性，推进事后数据应用集成化，解决监管留痕、监管合规、监管考核等问题。 平台同步建立线上全程留痕机制，通过将监管主体、监管措施、落实回复、监管流程、时间节点等信息实时自动形成监管日志，实现监管全流程记录公开透明化，做到监管可查询、可追溯、可倒查。 监管流程全程留痕 审判管理部门可通过重点案件可视化统计模块监管数据，对“四类案件”等重点案件的数量分布、标注状态、监管状态等情况，及时有效按月跟踪、统计，强化数据深度应用和运行态势分析，为监管工作提示、监管数据公示、院庭长工作决策提供数据支撑。 重点案件可视化界面 自2022年11月“四类案件”信息化智能监管平台在嘉定法院上线以来，共标注“四类案件”等重点案件411件，其中平台自动识别推送72件，立案庭和承办审判组织标注280件，院庭长和职能部门推送59件，共发出近千条监管措施。嘉定法院建立了典型重点案件全周期节点跟踪培育机制，推动上海市首例骗取长护险基金案完成典型案例成果转化。 / 结语/ 下一步，嘉定法院将紧紧围绕“政治建设引领、司法质效为本、数字改革赋能”的工作主线，不断完善优化“四类案件”信息化智能监管平台功能，进一步拓展案件情形自动识别、案件监管精准把控、案件数据智慧交互等数字应用场景，构建全流程立体化智能化审判监督管理体系，加快推进审判体系和审判能力现代化，以数字化赋能法治化，以法治化规范数字化，努力创造更高水平的数字正义。 END 好书推荐 《中国智慧法院建设丛书》是一套政务信息化建设工具书，已出版7册。2021年4月首批出版5册图书，2023年8月第二批出版2册，分别为《智慧法院区块链的研究与实践》《复杂信息系统体系工程方法——结合智慧法院建设开展的探索实践》。丛书深入总结了智慧法院建设体系工程方法、标准体系和评估体系、优秀案例、地方实践、互联网司法实践、区块链研究与实践、复杂信息系统体系的工程方法，丛书集信息技术的理论研究、实践应用、经验成果推广应用、技术操作于一体，既适用于智慧法院建设者，也适用于智慧法院科技创新研究者，对其他行业信息化建设具有借鉴意见。 点击图片 立购丛书 作者：王琦炜、李文苑 单位：上海市嘉定区人民法院 本期责编：吴晨晨 特别提示：凡本公众号注明“来源”的作品均转载自媒体，部分配图来源于网络，版权归原作者所有，如有侵权，请联系删除。所分享内容为作者本人观点，仅供读者学习参考。欢迎提出建议，惠赐作品。唯一投稿渠道邮箱：xinxuan@court.gov.cn（互联网），邮件请一并抄送至xinxuantg@163.com。\",\"source_name\":\"微信公众号\",\"similarvolume\":0}],\"negative\":[{\"emotion\":3,\"article_public_id\":\"a0e54034a4c9a9e18fe0c7a5848b639c\",\"publish_time\":\"2023-10-16 11:05:00\",\"key_words\":\"{\\\"服务投诉\\\":1,\\\"分析报告\\\":1,\\\"乱收费\\\":1,\\\"消费者\\\":3,\\\"商品类\\\":1,\\\"消保委\\\":1,\\\"许海燕\\\":1,\\\"上门费\\\":1,\\\"消费投诉\\\":2,\\\"第三季度\\\":2,\\\"受理投诉\\\":1}\",\"title\":\"三季度全省受理消费投诉5.5万件 为消费者挽损4783万余元\",\"_score\":\"13.73\",\"content\":\"(记者 许海燕 洪叶) 10月9日，省消保委发布第三季度消费投诉和舆情分析报告。第三季度，共受理投诉55545件，接待消费者来访和咨询9.75万人次，投诉数据同比下降13.97%，为消费者挽回经济损失4783万余元。其中，商品类投诉达27420件，占受理总量的49.37%;服务类达27558件，占比49.61%;其他商品和服务投诉达567件，占比1.02%。 报告显示，消费投诉热点主要涉及汽车、医疗、房产、教育、旅游等行业和领域。家电安装维修投诉多，存在免费安装却收上门费、虚假维修、巧立名目乱收费、冒充官方维修人员、售后服务差、不履行三包承诺等问题。研学游成为今年暑期热门话题，存在研而不学、打卡走形式、价格虚高、研学机构鱼龙混杂、导师资质、食宿安全等问题。房屋装修顽疾难破，设计师不专业、口头承诺没有写入合同、工程延期、价格超预算、恶意增项等现象频发，消费者面临维权难困境。共享电动车不配安全头盔、共享按摩椅服务问题和安全隐患、共享办公室纠纷等一系列共享经济领域问题频发。早教托育服务待规范，早教托育领域商家跑路、退费问题等引发舆论关注。\",\"source_name\":\"苏讯网\",\"similarvolume\":0}],\"positive\":[]}', '[{\"x\":\"嘉定法院\",\"value\":9},{\"x\":\"机器人\",\"value\":8},{\"x\":\"信息化\",\"value\":7},{\"x\":\"游戏社区\",\"value\":7},{\"x\":\"公众号\",\"value\":6},{\"x\":\"我们可以\",\"value\":6},{\"x\":\"智慧法院\",\"value\":5},{\"x\":\"药品监管\",\"value\":5},{\"x\":\"国家药监局\",\"value\":5},{\"x\":\"流通领域\",\"value\":4},{\"x\":\"微信公众号\",\"value\":4},{\"x\":\"自动识别\",\"value\":4},{\"x\":\"游戏厂商\",\"value\":3},{\"x\":\"活跃度\",\"value\":3},{\"x\":\"周阅读量\",\"value\":3},{\"x\":\"更好地\",\"value\":3},{\"x\":\"管理工具\",\"value\":2},{\"x\":\"标签化\",\"value\":2},{\"x\":\"广东省药监局\",\"value\":2},{\"x\":\"传媒集团\",\"value\":2},{\"x\":\"创造力\",\"value\":2},{\"x\":\"投诉举报\",\"value\":2},{\"x\":\"自定义\",\"value\":2},{\"x\":\"消费投诉\",\"value\":2},{\"x\":\"上海市嘉定区人民法院\",\"value\":2},{\"x\":\"食品药品\",\"value\":2},{\"x\":\"职能部门\",\"value\":2},{\"x\":\"小游戏\",\"value\":2},{\"x\":\"舆情监测系统\",\"value\":2},{\"x\":\"数据分析\",\"value\":2},{\"x\":\"人工耳蜗\",\"value\":2},{\"x\":\"中国医药\",\"value\":2},{\"x\":\"上海市高级人民法院\",\"value\":2},{\"x\":\"第三季度\",\"value\":2},{\"x\":\"湖北药监\",\"value\":2},{\"x\":\"参考库\",\"value\":2},{\"x\":\"扫黑除恶\",\"value\":2},{\"x\":\"中国健康传媒集团\",\"value\":2},{\"x\":\"违法交易\",\"value\":1},{\"x\":\"药安食美诚信\",\"value\":1},{\"x\":\"服务投诉\",\"value\":1},{\"x\":\"审管办\",\"value\":1},{\"x\":\"国家市场监督管理总局\",\"value\":1},{\"x\":\"协助院\",\"value\":1},{\"x\":\"反不正当竞争法\",\"value\":1},{\"x\":\"宣传活动\",\"value\":1},{\"x\":\"消保委\",\"value\":1},{\"x\":\"江西省泰和县市场监督管理局\",\"value\":1},{\"x\":\"新格局\",\"value\":1},{\"x\":\"最高人民法院\",\"value\":1},{\"x\":\"用户消费\",\"value\":1},{\"x\":\"上海市\",\"value\":1},{\"x\":\"乱收费\",\"value\":1},{\"x\":\"俄罗斯\",\"value\":1},{\"x\":\"上海高院\",\"value\":1},{\"x\":\"中国医药报社\",\"value\":1},{\"x\":\"湖北省药监局\",\"value\":1},{\"x\":\"咸宁市市场监管局\",\"value\":1},{\"x\":\"分析报告\",\"value\":1},{\"x\":\"整治工作\",\"value\":1},{\"x\":\"黑恶势力\",\"value\":1},{\"x\":\"承诺书\",\"value\":1},{\"x\":\"李文苑\",\"value\":1},{\"x\":\"商品类\",\"value\":1},{\"x\":\"社区活动\",\"value\":1},{\"x\":\"许海燕\",\"value\":1},{\"x\":\"上门费\",\"value\":1},{\"x\":\"为基础\",\"value\":1},{\"x\":\"执法人员\",\"value\":1},{\"x\":\"量身定制\",\"value\":1},{\"x\":\"新闻网讯\",\"value\":1},{\"x\":\"无照经营\",\"value\":1},{\"x\":\"受理投诉\",\"value\":1}]', '[{\"trend\":1,\"count\":583,\"index\":\"29.34%\",\"keyword\":\"公众号\",\"value_chain\":415},{\"trend\":1,\"count\":540,\"index\":\"27.18%\",\"keyword\":\"信息化\",\"value_chain\":284},{\"trend\":1,\"count\":399,\"index\":\"20.08%\",\"keyword\":\"机器人\",\"value_chain\":241},{\"trend\":1,\"count\":353,\"index\":\"17.77%\",\"keyword\":\"我们可以\",\"value_chain\":285},{\"trend\":1,\"count\":53,\"index\":\"2.67%\",\"keyword\":\"国家药监局\",\"value_chain\":17},{\"trend\":1,\"count\":40,\"index\":\"2.01%\",\"keyword\":\"药品监管\",\"value_chain\":5},{\"trend\":1,\"count\":11,\"index\":\"0.55%\",\"keyword\":\"流通领域\",\"value_chain\":6},{\"trend\":1,\"count\":4,\"index\":\"0.2%\",\"keyword\":\"智慧法院\",\"value_chain\":0},{\"trend\":1,\"count\":2,\"index\":\"0.1%\",\"keyword\":\"嘉定法院\",\"value_chain\":0},{\"trend\":1,\"count\":2,\"index\":\"0.1%\",\"keyword\":\"游戏社区\",\"value_chain\":0}]', '{\"total\":5,\"total_site\":4,\"sites\":[{\"rate\":\"40%\",\"name\":\"手机新浪网\",\"count\":2,\"logo\":\"https://mjs.sinaimg.cn/wap/module/base/img/favicon.ico\",\"id\":\"8d1d17dbf4627b988ccf9b8f02833aaa\"},{\"rate\":\"20%\",\"name\":\"微信公众号\",\"count\":1,\"logo\":\"https://upload.shejihz.com/2020/02/226fb192bb7c53ecea6825bd69aaac38.jpg\",\"id\":\"15fcd6bfe79c74baa079601056204e79\"},{\"rate\":\"20%\",\"name\":\"搜狐号\",\"count\":1,\"logo\":\"https://www.sohu.com/favicon.ico\",\"id\":\"11d5b64c146b77cfb31b2ca733f2e539