INSERT INTO `monitor_analysis` VALUES (3612, '2023-10-16 19:51:33', 1713885385949712384, 1493155905573883904, 3, '{\"all\":{\"rate\":100,\"count\":34},\"earlyWarning\":{\"rate\":0.0,\"count\":0,\"dataSwitch\":\"close\"},\"noSensitive\":{\"rate\":82.35,\"count\":28},\"sensitive\":{\"rate\":17.65,\"count\":6}}', '{\"rate\":{\"negative\":\"17.65%\",\"neutral\":\"76.47%\",\"positive\":\"5.88%\"},\"chart\":[[\"正面\",2],[\"负面\",6],[\"中性\",26]]}', '[{\"rate\":\"61.11%\",\"count\":22,\"keyword\":\"舆情监测\"},{\"rate\":\"16.67%\",\"count\":6,\"keyword\":\"舆情分析\"},{\"rate\":\"13.89%\",\"count\":5,\"keyword\":\"舆情监控\"},{\"rate\":\"5.56%\",\"count\":2,\"keyword\":\"网络舆情监测\"},{\"rate\":\"2.78%\",\"count\":1,\"keyword\":\"舆情系统\"}]', '{\"text\":\"您一共设置了10个关键词。正面占比最高的是【舆情监测】到达100%。其次是【网络舆情监测】到达0.00%。负面占比最高的是【舆情分析】到达66.67%。其次是【舆情监控】到达33.33%。\",\"chart\":[[\"2023-10-09\",1,0],[\"2023-10-10\",6,0],[\"2023-10-11\",4,0],[\"2023-10-12\",1,2],[\"2023-10-13\",7,0],[\"2023-10-14\",5,2],[\"2023-10-15\",1,1],[\"2023-10-16\",3,1]]}', '{\"all\":[{\"emotion\":2,\"article_public_id\":\"ea02d0d9f16db2081367238f4bffa935\",\"publish_time\":\"2023-10-13 17:19:01\",\"key_words\":\"{\\\"市住建局\\\":1,\\\"人民群众\\\":1,\\\"线上线下\\\":1,\\\"网络舆情\\\":4,\\\"商丘市住房和城乡建设局\\\":1,\\\"政策解读\\\":1,\\\"张新中\\\":1,\\\"突发事件\\\":1,\\\"网络平台\\\":1,\\\"会议强调\\\":1,\\\"分析会\\\":1,\\\"网络舆情监测\\\":1,\\\"主持会议\\\":1}\",\"title\":\"市住建局组织召开网络舆情研讨分析会_工作动态_政务动态_商丘市住房和城乡建设局\",\"_score\":\"28.08\",\"content\":\"　　10月12日，市住建局组织召开网络舆情研讨分析会，传达学习《商丘市住房和城乡建设局网络舆情应对处置工作机制》，安排部署网络舆情监测和处置工作，副局长张新中主持会议并讲话。 　　会议强调，一要高度重视，深化认识。要建立以防控和引导并重的理念，及时披露客观事实，消除人民群众的疑虑。二要科学研判，防治结合。对可能引发重大网络舆情的突发事件、热点、敏感问题，要提前做好应对处置准备，反应迅速及时，措施把控得当，方法灵活有效。三要抓好网络舆情主阵地建设。要借助网络平台，加大政策解读和工作宣传力度，及时回应网民关切，进行线上线下互动，早发现、早报告、早协调、早处置，各部门要进行有效沟通，推动舆情处置工作高效开展。\",\"source_name\":\"商丘市住房和城乡建设局\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"a92945d86770c2c7cd3ab7399077b92e\",\"publish_time\":\"2023-10-11 09:27:47\",\"key_words\":\"{\\\"河南大河网数字科技有限公司\\\":2,\\\"中国科学院软件研究所\\\":1,\\\"王选新闻科学技术奖\\\":3,\\\"数据产业\\\":1,\\\"新闻传媒\\\":1,\\\"科技奖项\\\":2,\\\"先进单位\\\":2,\\\"突出贡献\\\":1,\\\"新技术\\\":1,\\\"舆情系统\\\":4,\\\"国家科技奖励办公室\\\":1,\\\"大河网\\\":3,\\\"工作者联合会\\\":1,\\\"政务服务\\\":2}\",\"title\":\"河南唯一！我们获全国三等奖！\",\"_score\":\"20.46\",\"content\":\"10月8日，2023年度“王选新闻科学技术奖”（项目奖）获奖结果揭晓，河南大河网数字科技有限公司申报的大河舆情系统荣获三等奖。这是继2023年8月入选“2023年大数据产业优秀标杆”之后，大河舆情系统再获殊荣。 “王选新闻科学技术奖”是经国家科技奖励办公室批准设立的新闻界跨媒体的唯一科技奖项，是我国新闻传媒行业最高层次的科技奖项。中国新闻技术工作者联合会从2004年开始正式设立“王选新闻科学技术奖”，用于表彰对推动新闻科学技术进步作出突出贡献的组织和个人。 据河南大河网数字科技有限公司相关负责人介绍，大河舆情系统于2016年建立，2022年，大河网与中国科学院软件研究所共建新闻数据与政务数据联合实验室运用新技术，对系统进行升级再造。系统依托大河网党媒机关信息资源优势，通过对敏感信息进行7x24小时研判，建立实时预警机制，提供千人千面的定制化服务。 目前，大河舆情系统提供了基础版、专业版、五星版、智能版等多个版本，根据用户的定制方案，结合系统实时研判的敏感信息，进行相似新闻过滤、敏感程度排名，筛选出敏感程度排名比较高的信息，通过用户设置的预警方式进行推送，保障敏感信息及时准确送达用户手中，为用户下一步的决策提供依据。此外，该系统能够提供短信、微信、App等多种智能预警方式，覆盖渠道广，保证预警及时有效，接收查阅方便。 作为河南省首家重点新闻网站，大河网坚持“互联网+新闻”、“互联网+技术”双强发展战略，积极构建“新闻+技术+政务+服务+商务”运营生态，凭借强大的自主研发能力及优质的运维服务能力，开发建设和运维着省人大、省政府、省政协和省委宣传部、省文明办、省委党史研究室、省发展改革委、省财政厅、省住建厅、省交通运输厅、省乡村振兴局、省文物局等500多个政务服务项目，实现了党委、政府、人大、政协“四大班子”政务服务业务全覆盖，是河南省最大的政务服务供应商。大河舆情研究院先后获得中宣部舆情报送先进单位/先进个人，河南省舆情报送先进单位等荣誉称号。 来源：大河网 杨露露 责编：李瑞 审核：赵汉青\",\"source_name\":\"全国党媒信息公共平台\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"6d3f519e3f05dad008afcef4ea81276e\",\"publish_time\":\"2023-10-12 01:41:44\",\"key_words\":\"{\\\"舆情监控\\\":1,\\\"是什么\\\":1}\",\"title\":\"付文初\",\"_score\":\"18.15\",\"content\":\"一点点进步都要拿命来换！ 这根本就不是什么新闻，如果没有舆情监控。\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":1,\"article_public_id\":\"e0c5e7f0a67487138f0a7c42996aa772\",\"publish_time\":\"2023-10-10 07:11:00\",\"key_words\":\"{\\\"旅游舆情\\\":1,\\\"监测分析\\\":1,\\\"线上线下\\\":1,\\\"新浪微博\\\":1,\\\"自定义\\\":1,\\\"舆情监测\\\":3,\\\"大数据平台\\\":1,\\\"人民网\\\":1,\\\"智慧旅游\\\":1,\\\"今日头条\\\":1,\\\"目的地\\\":3,\\\"智慧景区\\\":1,\\\"互联网\\\":2,\\\"新华网\\\":1,\\\"大数据\\\":2}\",\"title\":\"海鳗云\",\"_score\":\"16.44\",\"content\":\"「海鳗云旅游大数据」「智慧旅游平台」「智慧景区」「舆情监测」「旅游舆情」「旅游客流」海鳗旅游大数据平台是基于海鳗云对外部高价值数据强大的采集、处置与分析能力，结合景区综合管控及政府行业监管的典型场景，创造性推出成熟的一体化旅游目的地大数据监测分析平台，全面支持管理、营销和决策。平台整合了互联网内容数据、手机GPS位置数据、银联清算数据，数据类型丰富，覆盖面广。 1、互联网内部数据包含了携程、美团、途牛等OTA平台、新浪微博、今日头条、央视、新华网、人民网等，信源240万+， 2、手机GPS位置数据包含APP消息推送、运营、地图服务商等，每日LBS报点更新30亿+ 3、消费数据来自银联清算数据，原始数据为跨行清算数据，大量线上线下真实交易记录。 可以用完整而全面的指挥中心综合可视化大屏展示，还能自定义生成分析报告，为以下典型场景的分析决策提供支持。 1、行政监管 客流监测、消费监测、重大舆情监测、强制消费监测、低价游监测、诚信服务监管、目的地品牌监测 2、产品开发 潜在客流分析、旅游资源分析、目的地品牌评估、热点旅游产品分析、竞争监测 3、投资决策 目的地品牌评估、区域业态分析、目标客源分析、区域消费分析、竞品分析 4、精准营销 品牌评估、选取投放客群、选取投放渠道、精准投放、营销审计、营销效果评估 5、管理提升 旅游舆情监测、游客满意度分析、游客行为分析、游客消费分析\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"97798fa56ad94308909932412c07a239\",\"publish_time\":\"2023-10-14 09:57:00\",\"key_words\":\"{\\\"舆情监控\\\":1,\\\"不理不睬\\\":1,\\\"近两年\\\":1,\\\"工作室\\\":1,\\\"反过来\\\":1,\\\"冷冰冰\\\":1,\\\"人肉搜索\\\":1,\\\"大爆发\\\":1,\\\"了如指掌\\\":1,\\\"不知道\\\":1,\\\"时间长\\\":1,\\\"手拉手\\\":1,\\\"高高在上\\\":1,\\\"受不了\\\":1,\\\"最近两年\\\":1}\",\"title\":\"莲池bb机\",\"_score\":\"14.92\",\"content\":\"2007 李涛，这次跑粉严重还有个原因，就是他最近两年始终对粉丝冷冰冰，什么事情都是冷处理装死，他本人和工作室都这么干，但现在知道了，他和团队其实对粉圈的日常事情了如指掌，有舆情监控的。这次团建只是粉圈多年情绪累积后的大爆发而已。他近两年像被什么东西夺舍了一样，一副高高在上的样子，就是不营业，莲粉求了多少次了他就是不理不睬，全靠粉圈自己给自己提供情绪价值，还要反过来给他提供情绪价值和金钱精力，时间长了谁都受不了。别说大黑了，大黑的时候莲粉可是手拉手挡在他前面的，莲粉被整个饭圈霸凌他不会不知道的，莲粉拼命出钱出力人肉挡在他前面才有他的今天，莲粉被cyq人肉搜索，被饭圈各家围攻...莲粉怎么熬过来的？莲粉对他不离不弃最后得到的却是大黑结束他反而开始对莲粉冷淡不上心了，只有要花钱做事了才会出来营业一下。大粉也别洗脑什么出去看看其他艺人都没他对粉丝好，你们骗骗新粉mmr吧，我们都是老粉了，有些人的粉龄可能都比现在的一些大粉都长，真情还是假意还是能分的清楚的。\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"16cd5ca4578df09b0f35aa269d7fad8b\",\"publish_time\":\"2023-10-10 18:11:56\",\"key_words\":\"{\\\"新浪财经\\\":2,\\\"必要时\\\":1,\\\"目前有\\\":1,\\\"投资者提问\\\":1,\\\"黑公司\\\":1,\\\"免责声明\\\":1,\\\"查看更多\\\":1,\\\"舆情监测\\\":1,\\\"投资建议\\\":1,\\\"舆情管理\\\":1,\\\"董秘问答\\\":1,\\\"感谢您\\\":1,\\\"贵公司\\\":1,\\\"牧原股份\\\":1,\\\"准确性\\\":1}\",\"title\":\"投资者提问：目前抖音平台黑公司的很多，请问贵公司关注了没有，采取了何种措施...\",\"_score\":\"14.65\",\"content\":\"投资者提问： 目前抖音平台黑公司的很多，请问贵公司关注了没有，采取了何种措施？ 董秘回答(牧原股份SZ002714)： 您好，公司高度重视舆情管理，目前有专门团队进行舆情监测，密切关注相关信息，在必要时采取各类手段应对捏造散播不实信息、造谣诽谤、损害公司名誉的单位、个人。感谢您的关注！ 查看更多董秘问答>> 免责声明：本信息由新浪财经从公开信息中摘录，不构成任何投资建议；新浪财经不保证数据的准确性，内容仅供参考。\",\"source_name\":\"手机新浪网\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"d90dd77137c2eb6377f138b729fc8024\",\"publish_time\":\"2023-10-12 14:02:26\",\"key_words\":\"{\\\"深圳市委书记\\\":1,\\\"在这里\\\":9,\\\"你可以\\\":9,\\\"融媒体中心\\\":4,\\\"事业单位\\\":3,\\\"封面人物\\\":3,\\\"团市委\\\":14,\\\"共青团\\\":6,\\\"孟凡利\\\":1,\\\"文化艺术\\\":2,\\\"品牌建设\\\":1,\\\"深圳青年\\\":3,\\\"新媒体\\\":3,\\\"深圳市委\\\":1}\",\"title\":\"选拔10人！第三批“青宣工程”专业人才培养计划启动\",\"_score\":\"13.98\",\"content\":\"你是否热爱用镜头、用笔尖 记录生活点滴与美好？ 你是否每天 热点趣事尽在掌握 有一颗熊熊燃烧的创作之心？ 你是否还隐藏着 策划活动、统筹协调等 百般武艺？ 机会来了！ 团深圳市委实施第三批“青宣工程”专业人才培养计划，决定面向全市选拔“青宣工程”跟班学习干部10名。 团市委“青宣工程” 依托深圳共青团融媒体中心平台 联合各意向机关和事业单位 以及企业、高校、社会组织等 定向培养 宣传和品牌建设人才 “青春深圳”作为 深圳共青团融媒体中心 全媒介矩阵平台 近年来影响力迅速增长 代表青年发声愈发响亮 2023年3月6日，深圳市委书记孟凡利来到团市委调研，在深圳共青团融媒体中心与共青团和青年工作者交谈交流。他希望大家把握当下、面向未来，以奋斗姿态激扬青春，以实干实绩成就梦想，让成长之路更加出新出彩。 ▲点击阅读《孟凡利调研我市工会共青团妇女工作 围绕中心 服务大局 奋力推动全市群团工作高质量发展》 温度和深度，我们有 在这里 你可以挥动笔墨 用文字展示乍现的灵光 ▲点击阅读《五方面三十项青春动能，推进建设世界级青春之城》 ▲点击阅读《“逃出大英博物馆”引全网共鸣，敦煌文物“回家”唤醒了什么？》 ▲点击阅读《深圳到底“卷不卷”？答案是……》 在这里 你可以打磨一个好视频 用画面讲述动人瞬间 在这里 你可以讲好青年故事 让榜样的力量跃然纸上 ▲点击阅读《封面人物丨郭吉勇：从《洛神水赋》到《敦煌遗书》，他让“国潮”惊艳世界》 ▲点击阅读《封面人物丨时潇含：“我写的未必是深圳，但笔下无处不是深圳”》 ▲点击阅读《封面人物｜凌云：云出峨眉，凌耀沧海》 ▲点击阅读《封面人物丨林大叶：青春之城诞生的全国最年轻交响乐团首席指挥》 资源和平台，我们有 在这里 你可以组织参与各类活动 策划深圳青年自己的“文化菜单” ▲点击阅读《深圳青年读书月来啦！书香满城“阅”见青春》 ▲点击阅读《这届年轻人不懂戏？我：连追三场是真上瘾！》 ▲点击阅读《获奖名单出炉！第二届“用青春向人民报告”演讲大赛落幕》 ▲点击阅读《爱了！玉门关汉餐学汉礼，鸣沙山星空音乐会……这场定制旅行火了》 ▲点击阅读《@深圳青年，你的文化菜单来了，请点单！》 在这里 你可以与“明星”大咖面对面 让杰出伴你我成长 ▲点击阅读《榜样力量！2023年“深圳十大杰出青年”是他们》 在这里 你可以连接351万志愿者 传递雷锋精神和志愿文化 ▲点击阅读《深圳，一座“追锋”的城市》 在这里 你可以享受“高大上”的文化艺术展演 前排感受文化艺术的熏陶 专业和舒适，我们有 在这里 你可以体验专业的设备和演播厅 在广阔的舞台施展才华 在这里 你可以拥有舒适的办公环境 还能享用一杯特制“青春咖啡” 我们在“青春深圳”等你！ 如果你的企业需要培养 更擅长品宣设计和品牌运营的骨干力量 如果你的组织需要培养 更适合拓展青年应用场景的专业人才 如果你的单位需要培养 更熟悉青年工作和青年活动策划的团宣干部 团市委第三批“青宣工程” 10个席位等你来挑战！ 划重点！ “青宣工程”选拔具体要求 ↓↓↓ 选拔对象和条件 （一）党政机关、事业单位。全市各级党政机关、事业单位的团干部和青年干部，要求有一定宣传工作基础，文字能力强、综合素质高，具有较强的选题策划能力、组织协调能力和活动执行能力。 （二）高等学校（含高职院校）、中小学。全市各高校、中小学综合素质高、具有一定宣传工作基础的团干部、少先队辅导员和青年教师，要求具备青少年工作经验，有较强的语言表达和文字功底。 （三）企业和社会组织。各级、各领域企业的团干部和青年干部，要求有一定宣传工作基础，或相关专业学习经历，有活力、创造力。各社会团体、基金会、民办非企业单位等社会组织中的优秀青年也可报名。 跟班学习时间 跟班学习时间为6-12个月，不少于6个月。 跟班学习内容 （一）参与深圳共青团融媒体中心工作，包括宣传宣讲活动、青年榜样选树、新媒体编辑运营、视频拍摄制作、媒体专栏策划、专题片策划录制、大型活动组织实施，以及网络舆情监测应对和网络舆论引导等。 （二）参加团市委机关各项活动，参加专业授课、走访研学、外出交流等各项活动。 （三）跟班学习期间，将有计划地组织跟班学习干部加强与中央、省、市各级媒体，以及全市各级政务新媒体、团属媒体的工作联系，加强与各网络平台、互联网企业、网络“大V”的工作交流。 跟班学习管理 （一）跟班学习期间，不转行政、工资关系，团市委提供办公条件，提供公务出差差旅补贴等。跟班学习时间超过6个月的，应当转移党团组织关系，参加团市委机关的党团组织生活。要保障跟班学习期间人员在派出单位的薪资待遇，原则上薪资待遇不低于跟班学习之前的薪资待遇。 （二）符合条件的，跟班学习干部可在相关部门挂任相应职务，参与团市委各项重点工作组织策划和宣传执行。 （三）干部跟班学习期间须遵守团市委有关工作制度，服从团市委工作安排。原则上，除因职务调整变动等特殊原因外，不得提前结束跟班学习。 （四）跟班学习期满后，跟班学习干部回派出单位工作。个人需填写《跟班学习总结鉴定表》，团市委对其跟班学习期间的表现情况作出鉴定，并送达跟班学习干部所在单位党委（人事）部门存入个人档案。 选拔程序 （一）推荐人选经本单位党委（人事）部门同意后，按要求如实填写《团市委跟班学习干部推荐表（“青宣工程”专项）》（见附件）。由所在单位盖章并出具工作鉴定，于2023年10月31日前，将纸质材料上报团市委（可邮寄），同时将盖章扫描版发至工作邮箱szgqtrmt@163.com。 （二）各团区（工）委汇总本地区、本领域的推荐人选，审核确定后报团市委。各团区（工）委、各高校团委根据条件至少推荐1名跟班学习人选。鼓励优秀团干部和青年自荐报名，团市委视情况与人选所在单位加强沟通联系。 （三）团市委组织笔试、面试，研究确定跟班学习人选，根据宣传和新媒体工作需要，综合考虑跟班学习人选实际情况安排具体岗位。 心动不如行动 速速点击下方链接 查看和填写推荐表报名吧！ ↓↓↓ 推荐表.docx 这个展现你N多能力的舞台 只等你来！ End 编辑：张菁菁 审校：陶   铮 审核：刘   昊   团团发布   ▼关注\\\"青春深圳\\\"微信、抖音、快手、B站、 视频号\",\"source_name\":\"微信公众号\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"9cb3107559dfd912545f8e1af012a981\",\"publish_time\":\"2023-10-15 07:00:00\",\"key_words\":\"{\\\"服务投诉\\\":1,\\\"中国江苏网讯\\\":1,\\\"分析报告\\\":1,\\\"乱收费\\\":1,\\\"消费者\\\":3,\\\"商品类\\\":1,\\\"消保委\\\":1,\\\"许海燕\\\":1,\\\"上门费\\\":1,\\\"消费投诉\\\":2,\\\"第三季度\\\":2,\\\"受理投诉\\\":1}\",\"title\":\"三季度全省受理消费投诉5.5万件 为消费者挽损4783万余元\",\"_score\":\"13.73\",\"content\":\"中国江苏网讯 （记者 许海燕 洪叶） 10月9日，省消保委发布第三季度消费投诉和舆情分析报告。第三季度，共受理投诉55545件，接待消费者来访和咨询9.75万人次，投诉数据同比下降13.97%，为消费者挽回经济损失4783万余元。其中，商品类投诉达27420件，占受理总量的49.37%；服务类达27558件，占比49.61%；其他商品和服务投诉达567件，占比1.02%。 报告显示，消费投诉热点主要涉及汽车、医疗、房产、教育、旅游等行业和领域。家电安装维修投诉多，存在免费安装却收上门费、虚假维修、巧立名目乱收费、冒充官方维修人员、售后服务差、不履行三包承诺等问题。研学游成为今年暑期热门话题，存在研而不学、打卡走形式、价格虚高、研学机构鱼龙混杂、导师资质、食宿安全等问题。房屋装修顽疾难破，设计师不专业、口头承诺没有写入合同、工程延期、价格超预算、恶意增项等现象频发，消费者面临维权难困境。共享电动车不配安全头盔、共享按摩椅服务问题和安全隐患、共享办公室纠纷等一系列共享经济领域问题频发。早教托育服务待规范，早教托育领域商家跑路、退费问题等引发舆论关注。\",\"source_name\":\"东方号\",\"similarvolume\":0},{\"emotion\":2,\"article_public_id\":\"c32e33d892a1e4a38d0dc940f59fa26e\",\"publish_time\":\"2023-10-10 10:36:36\",\"key_words\":\"{\\\"枫桥经验\\\":4,\\\"陈启武\\\":1,\\\"获得感\\\":1,\\\"人满意度\\\":1,\\\"安排专人\\\":1,\\\"君山区政务服务中心\\\":1,\\\"积极推动\\\":1,\\\"全媒体\\\":1,\\\"纳税人\\\":3,\\\"灵活就业人员养老保险\\\":1,\\\"新时代\\\":2,\\\"灵活就业养老保险\\\":1,\\\"舆情分析\\\":1,\\\"国家税务总局岳阳市君山区税务局\\\":1,\\\"王先生\\\":1}\",\"title\":\"君山区税务局：践行“枫桥经验” 助力征纳和谐\",\"_score\":\"12.99\",\"content\":\"岳阳日报全媒体讯（陈启武）今年以来，国家税务总局岳阳市君山区税务局积极践行新时代“枫桥经验”，听企声、解难题、化争议，以“三心”举措，化解涉税矛盾，助力征纳和谐，推动“枫桥经验”在税收工作中落地生根、开花结果。 为不断提升纳税人缴费人满意度获得感，该局接诉即办，将缴费服务延伸到社保大厅和医保大厅，安排专人到社保、医保窗口受理缴费业务，确保缴费不成功、审批不畅通等问题零发生。“从首次参保到灵活就业养老保险缴费成功，在社保窗口就可以一次性办妥，真是太方便了，为税务的延伸服务点赞。”前来君山区政务服务中心缴纳灵活就业人员养老保险的王先生高兴地说道。 “枫桥经验”的核心是依靠群众、化解矛盾、促进和谐、推动发展。在税务工作中，该局推出“细心调解、耐心辅导、贴心服务”“三心”举措，积极推动征纳关系的和谐发展。建立了税费服务诉求和舆情分析办理联席会议制度，依托“幸福税务工作室”专家团队，听企声、解难题、化争议。专家团队成员轮岗常驻办税大厅着力解决纳税人缴费人办税“疑难杂症”，定期到重点企业进行“把脉问诊”，开通“办问协同”线上可视化为纳税人缴费人答疑解惑。截至目前，已召开联席会议31次，解决堵点、难点89起，化解矛盾27起，确保了问题化解在基层，处理在一线。 君山区税务局党委委员、副局长柳海表示：“通过践行‘枫桥经验’，君山税务有效地促进了和谐征纳关系的发展，推动了税收工作的提升，实现了法律效果和社会效果的有机统一。下一步，我们将深入推进新时代‘枫桥式’税务所（分局、办税服务厅）建设，持续营造公平公正、和谐融洽的税收营商环境。”\",\"source_name\":\"岳阳网\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"e21c4956fdbf9d21bef2863713d148a8\",\"publish_time\":\"2023-10-14 17:54:00\",\"key_words\":\"{\\\"一段时间\\\":1,\\\"国家战略\\\":1,\\\"这方面\\\":1,\\\"新产品\\\":1,\\\"越来越\\\":1,\\\"生产力\\\":2,\\\"第四次工业革命\\\":1,\\\"确定性\\\":1,\\\"人工智能\\\":3,\\\"信息技术\\\":1,\\\"互联网\\\":2,\\\"一杯羹\\\":1,\\\"前不久\\\":1,\\\"发展趋势\\\":1}\",\"title\":\"只有人工没有智能\",\"_score\":\"12.91\",\"content\":\"原标题：只有人工没有智能 大家都很关心人工智能前景怎样？ 目前技术换代越来越快，似乎很难对「 AI 前景」做一个具体、明确的判断。前不久还经常有人吐槽人工智能“只有人工没有智能”，但没人能料到现在最火的 AI 是款可以和用户文字沟通的生成模型。 当然，尽管变数大于确定性，某些发展趋势和脉络还是清晰可见。 渗透现有行业（AI+） 人制造的机器表现出的智能，背后还是人的意志。 业内流传过一句话：AI 的技术程度有多高，背后的人力资源投入就有多大。 现在 AI 很火，谁都想分一杯羹，但掌握资源的还是那些成熟行业。 何况新产品最终还是得落到人的需求上，这方面早被现有行业开发完了。 互联网兴起后，从国家战略到企业运作都曾大兴“互联网+” 之风。（传统行业和互联网的融合） 以目前趋势来看，之后很长一段时间都会是“ AI +” 的时代。 人工智能将助力人类完成更多复杂工作，必然会改变工作流程、标准等细节。 比如：医疗领域的辅助诊断，教育方面的个性化学习，新闻行业的算法推荐、 AI 写稿等。 信息技术和智能技术分别引发了第三、第四次工业革命。 但现有的产业模式和工作体系，主要沿用的还是一两百年前发明的那套。 的确各方面一直都有变化，但满足的还是传统生产力需要，缺少根本性变革。 AI 作为先进生产力的代表，需要新的生产关系配合它发挥作用，也确实有深入调整工作关系和状态的可能性。 智子观测官网：http://zzgcz.com/，真正用人工智能大模型做最火热的电商舆情分析，系统升级中，敬请期待！\",\"source_name\":\"搜狐号\",\"similarvolume\":0}],\"negative\":[{\"emotion\":3,\"article_public_id\":\"6d3f519e3f05dad008afcef4ea81276e\",\"publish_time\":\"2023-10-12 01:41:44\",\"key_words\":\"{\\\"舆情监控\\\":1,\\\"是什么\\\":1}\",\"title\":\"付文初\",\"_score\":\"18.15\",\"content\":\"一点点进步都要拿命来换！ 这根本就不是什么新闻，如果没有舆情监控。\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"97798fa56ad94308909932412c07a239\",\"publish_time\":\"2023-10-14 09:57:00\",\"key_words\":\"{\\\"舆情监控\\\":1,\\\"不理不睬\\\":1,\\\"近两年\\\":1,\\\"工作室\\\":1,\\\"反过来\\\":1,\\\"冷冰冰\\\":1,\\\"人肉搜索\\\":1,\\\"大爆发\\\":1,\\\"了如指掌\\\":1,\\\"不知道\\\":1,\\\"时间长\\\":1,\\\"手拉手\\\":1,\\\"高高在上\\\":1,\\\"受不了\\\":1,\\\"最近两年\\\":1}\",\"title\":\"莲池bb机\",\"_score\":\"14.91\",\"content\":\"2007 李涛，这次跑粉严重还有个原因，就是他最近两年始终对粉丝冷冰冰，什么事情都是冷处理装死，他本人和工作室都这么干，但现在知道了，他和团队其实对粉圈的日常事情了如指掌，有舆情监控的。这次团建只是粉圈多年情绪累积后的大爆发而已。他近两年像被什么东西夺舍了一样，一副高高在上的样子，就是不营业，莲粉求了多少次了他就是不理不睬，全靠粉圈自己给自己提供情绪价值，还要反过来给他提供情绪价值和金钱精力，时间长了谁都受不了。别说大黑了，大黑的时候莲粉可是手拉手挡在他前面的，莲粉被整个饭圈霸凌他不会不知道的，莲粉拼命出钱出力人肉挡在他前面才有他的今天，莲粉被cyq人肉搜索，被饭圈各家围攻...莲粉怎么熬过来的？莲粉对他不离不弃最后得到的却是大黑结束他反而开始对莲粉冷淡不上心了，只有要花钱做事了才会出来营业一下。大粉也别洗脑什么出去看看其他艺人都没他对粉丝好，你们骗骗新粉mmr吧，我们都是老粉了，有些人的粉龄可能都比现在的一些大粉都长，真情还是假意还是能分的清楚的。\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"9cb3107559dfd912545f8e1af012a981\",\"publish_time\":\"2023-10-15 07:00:00\",\"key_words\":\"{\\\"服务投诉\\\":1,\\\"中国江苏网讯\\\":1,\\\"分析报告\\\":1,\\\"乱收费\\\":1,\\\"消费者\\\":3,\\\"商品类\\\":1,\\\"消保委\\\":1,\\\"许海燕\\\":1,\\\"上门费\\\":1,\\\"消费投诉\\\":2,\\\"第三季度\\\":2,\\\"受理投诉\\\":1}\",\"title\":\"三季度全省受理消费投诉5.5万件 为消费者挽损4783万余元\",\"_score\":\"13.73\",\"content\":\"中国江苏网讯 （记者 许海燕 洪叶） 10月9日，省消保委发布第三季度消费投诉和舆情分析报告。第三季度，共受理投诉55545件，接待消费者来访和咨询9.75万人次，投诉数据同比下降13.97%，为消费者挽回经济损失4783万余元。其中，商品类投诉达27420件，占受理总量的49.37%；服务类达27558件，占比49.61%；其他商品和服务投诉达567件，占比1.02%。 报告显示，消费投诉热点主要涉及汽车、医疗、房产、教育、旅游等行业和领域。家电安装维修投诉多，存在免费安装却收上门费、虚假维修、巧立名目乱收费、冒充官方维修人员、售后服务差、不履行三包承诺等问题。研学游成为今年暑期热门话题，存在研而不学、打卡走形式、价格虚高、研学机构鱼龙混杂、导师资质、食宿安全等问题。房屋装修顽疾难破，设计师不专业、口头承诺没有写入合同、工程延期、价格超预算、恶意增项等现象频发，消费者面临维权难困境。共享电动车不配安全头盔、共享按摩椅服务问题和安全隐患、共享办公室纠纷等一系列共享经济领域问题频发。早教托育服务待规范，早教托育领域商家跑路、退费问题等引发舆论关注。\",\"source_name\":\"东方号\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"e21c4956fdbf9d21bef2863713d148a8\",\"publish_time\":\"2023-10-14 17:54:00\",\"key_words\":\"{\\\"一段时间\\\":1,\\\"国家战略\\\":1,\\\"这方面\\\":1,\\\"新产品\\\":1,\\\"越来越\\\":1,\\\"生产力\\\":2,\\\"第四次工业革命\\\":1,\\\"确定性\\\":1,\\\"人工智能\\\":3,\\\"信息技术\\\":1,\\\"互联网\\\":2,\\\"一杯羹\\\":1,\\\"前不久\\\":1,\\\"发展趋势\\\":1}\",\"title\":\"只有人工没有智能\",\"_score\":\"12.91\",\"content\":\"原标题：只有人工没有智能 大家都很关心人工智能前景怎样？ 目前技术换代越来越快，似乎很难对「 AI 前景」做一个具体、明确的判断。前不久还经常有人吐槽人工智能“只有人工没有智能”，但没人能料到现在最火的 AI 是款可以和用户文字沟通的生成模型。 当然，尽管变数大于确定性，某些发展趋势和脉络还是清晰可见。 渗透现有行业（AI+） 人制造的机器表现出的智能，背后还是人的意志。 业内流传过一句话：AI 的技术程度有多高，背后的人力资源投入就有多大。 现在 AI 很火，谁都想分一杯羹，但掌握资源的还是那些成熟行业。 何况新产品最终还是得落到人的需求上，这方面早被现有行业开发完了。 互联网兴起后，从国家战略到企业运作都曾大兴“互联网+” 之风。（传统行业和互联网的融合） 以目前趋势来看，之后很长一段时间都会是“ AI +” 的时代。 人工智能将助力人类完成更多复杂工作，必然会改变工作流程、标准等细节。 比如：医疗领域的辅助诊断，教育方面的个性化学习，新闻行业的算法推荐、 AI 写稿等。 信息技术和智能技术分别引发了第三、第四次工业革命。 但现有的产业模式和工作体系，主要沿用的还是一两百年前发明的那套。 的确各方面一直都有变化，但满足的还是传统生产力需要，缺少根本性变革。 AI 作为先进生产力的代表，需要新的生产关系配合它发挥作用，也确实有深入调整工作关系和状态的可能性。 智子观测官网：http://zzgcz.com/，真正用人工智能大模型做最火热的电商舆情分析，系统升级中，敬请期待！\",\"source_name\":\"搜狐号\",\"similarvolume\":0},{\"emotion\":3,\"article_public_id\":\"331ff9056a4cf41938eac562be5909a3\",\"publish_time\":\"2023-10-12 10:27:00\",\"key_words\":\"{\\\"澎湃新闻\\\":1,\\\"共享经济\\\":17,\\\"余万元\\\":1,\\\"政策支持\\\":1,\\\"江苏全省\\\":1,\\\"安全隐患\\\":2,\\\"消费者投诉\\\":1,\\\"服务差\\\":1,\\\"消费者\\\":8,\\\"重灾区\\\":2,\\\"社会监督\\\":2,\\\"三季度\\\":1,\\\"江苏省消保委\\\":1,\\\"社会上\\\":1,\\\"行稳致远\\\":1}\",\"title\":\"投诉重灾区“共享经济”又该何去何从？\",\"_score\":\"10.53\",\"content\":\"原标题：投诉重灾区“共享经济”又该何去何从？ 近日，江苏省消保委发布第三季度投诉和舆情分析报告。三季度江苏全省消保委系统共受理投诉55545件，为消费者挽回经济损失4783余万元。报告中提及，共享经济是消费者投诉重灾区，存在产品服务差，部分共享商品计费方式混乱等问题。另外，部分共享类产品质量不过关，存在安全隐患，缺乏及时维护，卫生状况堪忧。也引发了消费者的不满与担忧。（澎湃新闻） 随着“共享”理念提出，“共享经济”逐渐在社会上崭露头角，尤其是互联网的助推下，更是将共享经济推向了高潮。共享经济能够快速发展既离不开网络时代的背景加持，当然更重要的是相关政策支持以及高度社会认可，不少商家和产业都享受到了“共享经济”带来的红利，但随着共享经济涉及内容不断增加，涉及领域不断扩大，同样也暴露出了很多短板问题，如产品盲目扩张，导致同质化严重 ；部分共享商品频繁涨价、易借难还，计费方式混乱；责任界定困难，部分商品缺少维修，存在较大安全隐患等等，这些问题不仅打消了消费者使用“念头”，也让共享经济陷入发展泥潭。 其实，共享经济最大的优势就是“共享”二字。结合社会发展需求产生的共享机制，可以直接了解供需，从而实现降低成本、精准匹配，实现了资源高度利用，同时，共享经济催生新的产业链，带动就业需求，缓解就业矛盾，成功契合社会生产生活节奏，因此其存在的价值十分重大，但要继续走远还是应坚持问题导向，靶向出击，国家、社会、经营主体以及消费者之间共同发力，才能使“共享经济”真正行稳致远。 首先，国家相关部门应及时完善配套监管制度，提升违法成本。一方面，可以出台针对共享经济的法律法规，形成完备的规则体系，提高企业准入门槛，对于一些不合规的条款或者服务，严格处理，以制度规范共享经济平稳运行。另一方面，规范行业标准统一化，缓解同质化阵痛，利用大数据有效分析潜在客户群体，根据分析结果统一制定市场价格、市场投入等标准，有效促进共享经济在发展中规范、在规范中发展。 其次，经营主体应尊重消费者主体地位，建立无差别服务网。其一，应完善配套维修服务，定时定点对投放区域相关产品进行维修，保障消费者的人身安全；其二，构建公平的价格机制，提前做好市场背调，合理公平定价计费，不能出现一个区域两种价格的闹剧，严格维护市场秩序；其三，完善售后反馈渠道，增加人工售后投放数量，同时利用数据网做好问题追踪反馈，给消费者一个满意的消费体验。 最后，共享经济能否健康运转离不开社会监督和消费者的共同维护。一方面，“共享经济”很大程度上与“社会自觉”相关关联，因此社会监督既能有效促进运营商提高产品服务质量，也能够避免出现无端破坏、过度使用等问题。另一方面，消费者要肩负起使用责任，享受产品服务的同时认真对待共享产品，这样才能形成良性循环，为共享产品延长“生命”，为共享经济续写“未来”。\",\"source_name\":\"搜狐号\",\"similarvolume\":0}],\"positive\":[{\"emotion\":1,\"article_public_id\":\"e0c5e7f0a67487138f0a7c42996aa772\",\"publish_time\":\"2023-10-10 07:11:00\",\"key_words\":\"{\\\"旅游舆情\\\":1,\\\"监测分析\\\":1,\\\"线上线下\\\":1,\\\"新浪微博\\\":1,\\\"自定义\\\":1,\\\"舆情监测\\\":3,\\\"大数据平台\\\":1,\\\"人民网\\\":1,\\\"智慧旅游\\\":1,\\\"今日头条\\\":1,\\\"目的地\\\":3,\\\"智慧景区\\\":1,\\\"互联网\\\":2,\\\"新华网\\\":1,\\\"大数据\\\":2}\",\"title\":\"海鳗云\",\"_score\":\"16.45\",\"content\":\"「海鳗云旅游大数据」「智慧旅游平台」「智慧景区」「舆情监测」「旅游舆情」「旅游客流」海鳗旅游大数据平台是基于海鳗云对外部高价值数据强大的采集、处置与分析能力，结合景区综合管控及政府行业监管的典型场景，创造性推出成熟的一体化旅游目的地大数据监测分析平台，全面支持管理、营销和决策。平台整合了互联网内容数据、手机GPS位置数据、银联清算数据，数据类型丰富，覆盖面广。 1、互联网内部数据包含了携程、美团、途牛等OTA平台、新浪微博、今日头条、央视、新华网、人民网等，信源240万+， 2、手机GPS位置数据包含APP消息推送、运营、地图服务商等，每日LBS报点更新30亿+ 3、消费数据来自银联清算数据，原始数据为跨行清算数据，大量线上线下真实交易记录。 可以用完整而全面的指挥中心综合可视化大屏展示，还能自定义生成分析报告，为以下典型场景的分析决策提供支持。 1、行政监管 客流监测、消费监测、重大舆情监测、强制消费监测、低价游监测、诚信服务监管、目的地品牌监测 2、产品开发 潜在客流分析、旅游资源分析、目的地品牌评估、热点旅游产品分析、竞争监测 3、投资决策 目的地品牌评估、区域业态分析、目标客源分析、区域消费分析、竞品分析 4、精准营销 品牌评估、选取投放客群、选取投放渠道、精准投放、营销审计、营销效果评估 5、管理提升 旅游舆情监测、游客满意度分析、游客行为分析、游客消费分析\",\"source_name\":\"微博\",\"similarvolume\":0},{\"emotion\":1,\"article_public_id\":\"3758362309456daea5faf066f8ebb2e0\",\"publish_time\":\"2023-10-11 07:00:15\",\"key_words\":\"{\\\"经营者\\\":6,\\\"旅游市场\\\":12,\\\"尹中卿\\\":3,\\\"旅游法\\\":35,\\\"以人为本\\\":2,\\\"次会议\\\":2,\\\"旅游业\\\":14,\\\"全国人大财经委\\\":3,\\\"千呼万唤中\\\":1,\\\"三十年磨一剑\\\":2,\\\"发展规划\\\":4,\\\"旅游者\\\":20,\\\"综合执法\\\":3,\\\"高质量发展\\\":3}\",\"title\":\"依法兴旅依法治旅 以人为本促进发展\",\"_score\":\"5.77\",\"content\":\"转自：中国旅游报 □ 本报首席记者 王 洋 “它‘三十年磨一剑’，在千呼万唤中出台”“它是一部以旅游者为核心，突出以人为本理念的法律”“它既是保障法，也是规范法，更是促进法”……今年10月1日，是《中华人民共和国旅游法》（以下简称“旅游法”）实施10周年。2013年4月25日，第十二届全国人民代表大会常务委员会第二次会议审议通过旅游法，并宣布同年10月1日正式实施。自此，我国旅游业发展开启了依法兴旅、依法治旅的新阶段。 “三十年磨一剑” 旅游法应运而生 利用公共资源建设的景区的门票，实行政府定价或者政府指导价，严格控制价格上涨；景区接待旅游者不得超过景区主管部门核定的最大承载量；旅行社不得以不合理的低价组织旅游活动……作为我国旅游业发展史上的第一部法律，旅游法是保障旅游者和旅游经营者的合法权益，规范旅游市场秩序，保护和合理利用旅游资源，促进旅游业持续健康发展的基本法。 回忆起这部法律的立法过程，作为10年前旅游法起草领导小组副组长，全国人大财经委原副主任委员尹中卿印象深刻。“旅游法前后酝酿了30年，才得以出台。它的诞生，取决于客观环境、时代的发展和人们的共识。” 早在改革开放初期，我国就启动了旅游法立法项目，国务院相关部门于1982年开始着手起草工作，第七届全国人大常委会和国务院在1988年均确立了立法计划。但因为当时我国旅游业发展尚处于起步阶段，有关方面未就立法领域的一些重点问题达成共识，旅游法立法工作一直没有实质性进展。 直到2009年，旅游法立法工作再次提上日程。12月18日，全国人大财经委牵头组织国家发改委、原国家旅游局等23个部门和有关专家成立旅游法起草组，并召开起草组第一次全体会议。 随后，旅游法起草工作紧张有序地进行。2012年3月14日，全国人大财经委第64次全体会议审议并通过了旅游法草案。历经三审和数次修改，旅游法终于在2013年4月25日下午召开的十二届全国人大常委会第二次会议上表决通过。 “旅游法从头到尾的设计都以旅游者为核心，突出以人为本的理念，专设了旅游者一章，明确旅游者权利义务，这在世界各国旅游立法中也是一大亮点。”在尹中卿看来，旅游法坚持以保障旅游者合法权益为主线，平衡旅游者与旅游经营者和旅游从业人员之间的权利、义务和责任，强化政府及其有关部门监管，规范旅游市场秩序，保护和合理利用资源，促进旅游业持续健康发展，这些思路均具有前瞻性。 “旅游法的颁布实施，是中国旅游人几十年的期盼。”在中国旅行社协会副会长、秘书长孙桂珍看来，它对保障旅游者和旅游经营者合法权益、规范旅游市场秩序、保护和合理利用旅游资源、促进旅游业持续健康发展起到了重要作用。 “旅游法的颁布实施是对旅游者合法权益的有效保障，也是对导游的合法权益的全面保障。在旅游法颁布之前，像我一样的导游基本上没有社保、医保，有时候连最基本的薪酬都难以保障。”刚刚获得第五届全国导游大赛“金牌导游员”称号的李杰对此感触颇深。“旅游法实施十年来，情况大有改观，让导游带团更安心了。” 北京市法学会旅游法学研究会常务理事杨洪浦认为，旅游法实施十年来充分证明：“它作为一部行业法是必要的，在促进旅游行业健康可持续发展、保障旅游者合法权益、规范旅游经营主体和从业人员行为等方面发挥了不可替代的积极作用。” “十年回头看” 依法治旅显成效 十载光阴，见证未改初心。旅游法出台伊始，全国各级文化和旅游主管部门、旅游行业深入学习宣传，完善相关配套制度和工作机制。 十年来，全国已出台200多部旅游法律、法规、规章，内容涉及吃、住、行、游、购、娱等旅游各领域，有力促进了旅游业高质量发展。 十年来，完善旅游综合协调和投诉执法机制，推动设立旅游警察、旅游巡回法庭、市场监管旅游分局等，完善多部门旅游市场监管责任清单，促进联合执法。 十年来，科学编制旅游发展规划，促进旅游产业发展，推动以国务院名义连续印发实施“十三五”和“十四五”旅游业发展规划，出台“一带一路”文化和旅游发展行动计划、大运河文化和旅游融合发展规划等专项规划，为旅游业发展指明方向。 十年来，加强旅游基础设施建设和旅游公共服务，据不完全统计，全国已建设各级各类游客服务中心（咨询中心、咨询点）3500多个，为旅游者提供交通、气象、住宿、餐饮、安全警示等旅游信息服务。 十年来，加大旅游执法力度，规范旅游市场秩序，强化旅游安全监管，特别是围绕未经许可经营旅行社业务、“不合理低价游”等旅游市场突出问题，持续开展专项整治行动，集中打击违法违规行为，遏制旅游违法违规现象。 “旅游法是支持、保护旅游业发展的重要制度基石和法律支撑。”海南省旅游和文化广电体育厅政策法规处有关负责人介绍，海南国际旅游岛建设启动以来，全省旅游持续火爆，旅游市场的违法违规行为也时有发生。“海南省委、省政府依照旅游法，积极开展旅游消费市场综合整治行动，以执法检查、立案查处、投诉处理、舆情监测、信用管理等举措，持续严厉打击扰乱旅游市场的顽症痼疾，保持旅游市场整治高压态势。” “旅游法的出台引领了包括黄山市在内的地方旅游立法工作。黄山市在获得地方立法权后，即着手开展旅游立法工作，出台了全国首部市级研学管理地方性法规《黄山市研学旅行管理暂行办法》。”黄山市文化和旅游局党组书记、局长吴小胜介绍，“当前，黄山正在推进民宿立法，通过立法将旅游改革决策及时法律化、规范化，让改革成果上升为统一意志，保证改革措施的稳定性，更好地释放改革红利。” “旅游法对保护和合理开发全省旅游资源、保障旅游者和旅游经营者合法权益、维护旅游市场秩序等起到了重要作用。”甘肃省文化和旅游厅政策法规处处长季慧琳介绍，近两年，省文化和旅游厅以推进文化和旅游市场综合执法队伍建设为重点，积极推进各项改革任务落实落地，探索打造“信用监管+专项执法”新模式，推动市场主体诚信经营意识不断增强、市场环境进一步净化。 在北京市文化市场综合执法总队四级高级主办贾玉峰看来：“旅游法在民事责任方面，对旅游者的知情权、合同解除权、合同转让权以及赔偿权等进行了比较详细的规定，最大限度保障了旅游者的合法权益。” “旅游法从根本上规范了旅游企业的经营规则，为企业合法开展业务和长期发展提供了法律保障。”春秋旅游副总经理周卫红介绍，“从最初的全员学习到对照条文进行业务流程和内部管理的调整、规范，旅游法有力促进了企业发展。” 聚焦高质量 与时俱进谋新局 良法善治，民心所向。旅游业是满足人民群众美好生活和精神文化需求的幸福产业，在全面依法治国的当今时代尤其需要法治保障。自2013年颁布实施以来，旅游法根据机构改革和“放管服”改革需要，历经2016年、2018年两次适应性修正，未来还将不断修订完善，与时俱进守护幸福产业、守护美好生活。 “党的二十大报告指出，高质量发展是全面建设社会主义现代化国家的首要任务。建议围绕旅游业高质量发展问题，全面总结近年来我国旅游政策和旅游立法的实践经验。”湖南师范大学法学院教授、副院长，文化和旅游研究院执行院长周刚志建议，可以根据红色旅游、研学旅游等发展需要，适当扩大旅游法的调整范围，设置相关条款回应国家公园、国家文化公园建设等问题；根据旅游市场秩序治理的需要，强化旅游综合协调机制的职能与旅游发展规划的作用；在“旅游规划与促进”部分增加融合发展内容等。 “旅游法明确界定了旅游管理者、旅游经营者和旅游者的权利义务，构建起政府统筹、部门负责、有分有合的旅游综合协调机制，对我国旅游业健康稳定发展有着十分重要的影响。”重庆市文化和旅游发展委员会市场管理处处长陈永雄建议，进一步明确导游群体的职业保障措施，科学确定旅行社和导游的关系，明确对专职导游和兼职导游的管理。 “完善旅游法，离不开旅游业改革发展的客观实际。”吴小胜从旅游实务角度提出建议，“一是可以在旅游法中突出旅游市场综合协调的作用，推动构建多部门综合治理涉旅市场的体制；二是进一步明确‘旅游’的含义，解决涉旅新业态没有法律规范的难题、困境，为新业态发展构建全方位的保护屏障；三是增设条款强化旅游合同监管，强化旅游经营者履约意识和旅游者守约意识。” “需要尽快推动修订完善旅游法条款内容，便于基层执法人员更便捷地运用法律手段规范市场秩序，当然，加强文化综合执法队伍建设，也是规范市场秩序的根本所在。”广西壮族自治区文化和旅游厅文化市场综合行政执法局有关负责人建议。 尹中卿说：“下一步修法时，应当考虑增加一些处罚或者强化管理措施。保护旅游者再硬气一点，让牙齿再锋利一点，效果可能会更好一些。”同时，他建议，在旅游法中加入有关公益诉讼的内容。“制定旅游法的时候，还没有公益诉讼这一制度。对于一些涉及众多旅游者的案件，适当引入公益诉讼制度，会更有利于旅游者维权。”\",\"source_name\":\"手机新浪网\",\"similarvolume\":0}]}', '[{\"x\":\"旅游法\",\"value\":35},{\"x\":\"化妆品\",\"value\":21},{\"x\":\"不符合规定\",\"value\":21},{\"x\":\"旅游者\",\"value\":20},{\"x\":\"岳池县\",\"value\":18},{\"x\":\"共享经济\",\"value\":17},{\"x\":\"旅游业\",\"value\":14},{\"x\":\"团市委\",\"value\":14},{\"x\":\"国家药监局\",\"value\":12},{\"x\":\"旅游市场\",\"value\":12},{\"x\":\"房地产\",\"value\":12},{\"x\":\"穿透式\",\"value\":11},{\"x\":\"传媒集团\",\"value\":11},{\"x\":\"经检验\",\"value\":10},{\"x\":\"医疗器械\",\"value\":10},{\"x\":\"中国健康传媒集团\",\"value\":10},{\"x\":\"食品安全\",\"value\":9},{\"x\":\"你可以\",\"value\":9},{\"x\":\"嘉定法院\",\"value\":9},{\"x\":\"在这里\",\"value\":9},{\"x\":\"湖南正桥公司\",\"value\":8},{\"x\":\"机器人\",\"value\":8},{\"x\":\"存款保险\",\"value\":7},{\"x\":\"四川省药监局\",\"value\":7},{\"x\":\"信息化\",\"value\":7},{\"x\":\"的通告\",\"value\":7},{\"x\":\"游戏社区\",\"value\":7},{\"x\":\"市场监管\",\"value\":7},{\"x\":\"鑫仁泰药业\",\"value\":7},{\"x\":\"贷款利率\",\"value\":6},{\"x\":\"公积金贷款\",\"value\":6},{\"x\":\"我们可以\",\"value\":6},{\"x\":\"脱贫攻坚\",\"value\":6},{\"x\":\"巩固拓展\",\"value\":6},{\"x\":\"共青团\",\"value\":6},{\"x\":\"监测中心\",\"value\":6},{\"x\":\"公众号\",\"value\":6},{\"x\":\"政策支持\",\"value\":5},{\"x\":\"广东省药监局\",\"value\":5},{\"x\":\"药品监管\",\"value\":5},{\"x\":\"舆情监测\",\"value\":5},{\"x\":\"新媒体\",\"value\":5},{\"x\":\"南少林药业\",\"value\":5},{\"x\":\"专项资金\",\"value\":5},{\"x\":\"奔跑吧\",\"value\":5},{\"x\":\"文化发展\",\"value\":5},{\"x\":\"中国医药\",\"value\":5},{\"x\":\"智慧法院\",\"value\":5},{\"x\":\"住房公积金贷款\",\"value\":4},{\"x\":\"乡村振兴\",\"value\":4},{\"x\":\"第一书记\",\"value\":4},{\"x\":\"占全省\",\"value\":4},{\"x\":\"各单位\",\"value\":4},{\"x\":\"银川市\",\"value\":4},{\"x\":\"广州天科\",\"value\":4},{\"x\":\"首付款\",\"value\":4},{\"x\":\"应用程序\",\"value\":4},{\"x\":\"沧州前卫医疗用品有限公司\",\"value\":4},{\"x\":\"安徽省\",\"v